<?php

namespace App\Tests\Entity;
use App\Entity\ImagesHabitat;

use Symfony\Bundle\FrameworkBundle\Test\KernelTestCase;

class ImagesHabitatTest extends KernelTestCase
{
    //Initialisation
    public function getEntity() : ImagesHabitat {
		return (new ImagesHabitat())
		->setChemin("chemin")
		->setPosition(0)
		->setCreatedAt(new \DateTimeImmutable());
	}

    public function assertHasErrors(ImagesHabitat $image, int $numberErrors = 0) {
		self::bootKernel();
		$error = self::getContainer()->get('validator')->validate($image);
		$this->assertCount($numberErrors, $error);
	}

    //Tests

    //Entité valide
    public function testValidEntity() {
		$this->assertHasErrors($this->getEntity(), 0);
	}

    //Tests des entrées invalides
    public function testInvalidPosition() {
		$this->assertHasErrors($this->getEntity()->setPosition(-1), 1);
	} 

    //Tests des entrées vides
    public function testEmptyChemin() {
		$this->assertHasErrors($this->getEntity()->setChemin(""), 1);
	}

    //Tests des entrées trop volumineuses
    public function testInvalidLenghtChemin() {
		$this->assertHasErrors($this->getEntity()->setChemin("
        
        
        
        123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789"), 1);
	}
}
