<?php

namespace App\Tests\Entity;
use App\Entity\Prestations;

use Symfony\Bundle\FrameworkBundle\Test\KernelTestCase;

class PrestationsTest extends KernelTestCase
{
    //Initialisation
    public function getEntity() : Prestations {
		return (new Prestations())
		->setNom("nom")
		->setIcone("icone")
		->setDescription("description")
		->setCreatedAt(new \DateTimeImmutable());
	}

    public function assertHasErrors(Prestations $prestation, int $numberErrors = 0) {
		self::bootKernel();
		$error = self::getContainer()->get('validator')->validate($prestation);
		$this->assertCount($numberErrors, $error);
	}

    //Tests

    //Entité valide
    public function testValidEntity() {
		$this->assertHasErrors($this->getEntity(), 0);
	}

    //Tests des entrées vides
    public function testEmptyNom() {
		$this->assertHasErrors($this->getEntity()->setNom(""), 1);
	}   
    public function testEmptyIcone() {
		$this->assertHasErrors($this->getEntity()->setIcone(""), 1);
	}

    //Tests des entrées trop volumineuses
    public function testInvalidLenghtNom() {
		$this->assertHasErrors($this->getEntity()->setNom("aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaz"), 1);
	}
    public function testInvalidLenghtIcone() {
		$this->assertHasErrors($this->getEntity()->setIcone("
        
        
        
        123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789"), 1);
	}
    public function testInvalidLenghtDescription() {
		$this->assertHasErrors($this->getEntity()->setDescription("
        
        
        
        123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789123456789"), 1);
	}
}
